<template>
  <div>
    <div class="reminder-container">
      <Timer :seconds="timeoutTimer.seconds" :minutes="timeoutTimer.minutes" :hours="timeoutTimer.hours" :days="timeoutTimer.days" />
    </div>
    <el-button color="#626aef" @click="reload()">Restart</el-button>
    <el-button @click="stopAlarmClock()">Stop Alarm Clock</el-button>
    <div class="duration-container" v-if="props.detailMode">
      <span class="duration-container-span-left">Duration: </span>
      <el-input-number v-model="duration" :controls="false"></el-input-number>
      <span class="duration-container-span-right">(s)</span>

      <span class="duration-container-span-2nd">Reminder Time: </span>
      <el-input-number v-model="reminderTime" :controls="false"></el-input-number>
      <span class="duration-container-span-right">(s)</span>
    </div>
  </div>
</template>

<style scoped>
.duration-container-span-left {
  font-size: 16px;
  margin-right: 10px;
}

.duration-container-span-right {
  font-size: 16px;
  margin-left: 10px;
}

.duration-container-span-2nd {
  font-size: 16px;
  margin-right: 10px;
  margin-left: 10px;
}
</style>

<script setup>
import { ref, watch, onMounted, getCurrentInstance } from "vue";
import { useTimer } from "vue-timer-hook";
import Timer from "./timer.vue";

const props = defineProps({
  expiryTimestamp: {
    type: Number,
    required: true,
  },
  detailMode: {
    type: Boolean,
    required: true,
  },
});

const duration = ref(60 * 15);
const audio = new Audio();
audio.autoplay = true;
audio.src =
  "data:audio/mpeg;base64,";

const timeoutTimer = useTimer(props.expiryTimestamp);

const reminderTime = ref(60 * 14);
const reminderAudio = new Audio();
reminderAudio.autoplay = true;
reminderAudio.src =
  "data:audio/mpeg;base64,";

const reminderTimer = useTimer(props.expiryTimestamp);

const reload = () => {
  const dateDuration = new Date();
  dateDuration.setSeconds(dateDuration.getSeconds() + duration.value);
  timeoutTimer.restart(dateDuration);

  const dateReminder = new Date();
  dateReminder.setSeconds(dateReminder.getSeconds() + reminderTime.value);
  reminderTimer.restart(dateReminder);

  audio.src =
    "data:audio/mpeg;base64,";

  reminderAudio.src =
    "data:audio/mpeg;base64,";

  audio.play();
  reminderAudio.play();
};

const stopAlarmClock = () => {
  audio.src =
    "data:audio/mpeg;base64,";
  timeoutTimer.pause();
};

const { appContext } = getCurrentInstance();

onMounted(() => {
  watch(
    () => timeoutTimer.isExpired.value,
    (newvalue, oldvalue) => {
      if (newvalue) {
        console.log(oldvalue);
        audio.src = require("/public/audio_timeout.mp3");
        appContext.config.globalProperties.$mitt.emit("timeoutEvent");
      }
    }
  ),
    watch(
      () => reminderTimer.isExpired.value,
      (newvalue, oldvalue) => {
        console.log(oldvalue);
        if (newvalue) {
          audio.src = require("/public/audio_reminder.mp3");
          appContext.config.globalProperties.$mitt.emit("timeoutEvent");
        }
      }
    );
});
</script>

<style scoped>
.reminder-container {
  display: flex;
  justify-content: center;
}

.duration-container {
  margin-top: 10px;
  margin-bottom: 10px;
}
</style>